// SPDX-License-Identifier: UNLICENSED

pragma solidity 0.8.18;

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";


interface AirdropClaim {
    function claimableAmount(address _addy) external view returns ( uint _claimableAmount) ;
    function claimedAmount(address _addy) external view returns ( uint _claimedAmount) ;
}

interface IStakedINTX {
    function lastTokenId() external view returns ( uint _lastTokenId) ;
    function ownerOf(uint256 tokenId) external view returns (address _owner) ;
    function balanceOfId(uint256 tokenId) external view returns (uint256 _xIntxBalance) ;
}

contract APIHelper is Initializable {

    constructor () { _disableInitializers(); }

    function initialize() public initializer {}

    function claimInfo(address[] calldata addresses, address _contract) external view returns ( uint[] memory amountClaimable,uint[] memory amountClaimed, uint[] memory amountLeft) {
        uint len = addresses.length;

        amountClaimable = new uint[](len); 
        amountClaimed = new uint[](len); 
        amountLeft = new uint[](len);

        AirdropClaim airdropClaim = AirdropClaim(_contract);

        uint claimable = 0;
        uint claimed = 0;
        uint left = 0;
        address addy = address(0);

        for ( uint i = 0; i < len; i = i+1) {
            addy = addresses[i];

            claimable = airdropClaim.claimableAmount(addy);
            claimed = airdropClaim.claimedAmount(addy);
            left = claimable-claimed;

            amountClaimable[i] = claimable;
            amountClaimed[i] = claimed;
            amountLeft[i] = left;
        }
    }

    function getTotalSupply ( IStakedINTX xIntx ) external view returns ( uint256 _totalSupply ) {
        uint256 _totalMinted = xIntx.lastTokenId();

        for( uint i = 0; i <= _totalMinted; i++) {
            try xIntx.ownerOf(i) {
                _totalSupply++;
            } catch {}
        }
    }

    function getTotalXIntxBalance ( IStakedINTX xIntx ) external view returns ( uint256 _xIntxTotalBalance ) {
        uint256 _totalMinted = xIntx.lastTokenId();

        uint256 _xIntxBalance;
        for( uint i = 0; i <= _totalMinted; i++) {
            _xIntxBalance = xIntx.balanceOfId(i);
            _xIntxTotalBalance = _xIntxTotalBalance + _xIntxBalance;
        }
    }

}

