// SPDX-License-Identifier: UNLICENSED

pragma solidity 0.8.18;

import "@openzeppelin/contracts/access/IAccessControl.sol";

interface AirdropClaim {
    function claimableAmount(address _addy) external view returns ( uint _claimableAmount) ;
    function claimedAmount(address _addy) external view returns ( uint _claimedAmount) ;
}

interface IStakedINTX {
    function lastTokenId() external view returns ( uint _lastTokenId) ;
    function ownerOf(uint256 tokenId) external view returns (address _owner) ;
    function balanceOfId(uint256 tokenId) external view returns (uint256 _xIntxBalance) ;
}

contract APIHelper2 {

	bytes32 public constant MULTIACCOUNT_ROLE = keccak256("MULTIACCOUNT_ROLE");
    bytes32 public constant DEFAULT_ADMIN_ROLE = 0x00;

    function isContract(address account) public view returns (bool) {
        uint256 size;
        assembly {
            size := extcodesize(account)
        }
        return size > 0;
    }

    function ourControl ( address[] calldata _partyAs, address multiaccount, address oldOwner) external view returns (address[] memory discriminatedPartyAs) {
        uint len = _partyAs.length;

        bool isOurs1;
        bool isOurs2;
        bool isOurs3;

        address partyA;

        discriminatedPartyAs = new address[](len);
        for ( uint i = 0; i < len; i = i+1) {
            partyA = _partyAs[i];

            isOurs3 = isContract(partyA);
            if ( isOurs3) {
                isOurs1 = IAccessControl( partyA ).hasRole(MULTIACCOUNT_ROLE, multiaccount);
                if ( isOurs1) {
                    isOurs2 = IAccessControl( partyA ).hasRole(DEFAULT_ADMIN_ROLE, oldOwner);
                    if ( isOurs2) {
                        discriminatedPartyAs[i] = partyA;
                    }
                }
            }
        }
    }

}

