contracts/IntentxWithdrawalBridge.sol.content:
// SPDX-License-Identifier: UNLICENSED

pragma solidity 0.8.18;

import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";

contract IntentxWithdrawalBridge is OwnableUpgradeable {
    using ECDSA for bytes32;
    using SafeERC20 for IERC20;

    // Constants and state variables
    address public intentxWhitelistChecker; // IntentX Trusted Address
    IERC20 public tradingToken;

    uint256 public amountProvided;
    uint256 public amountAvailable;

    // Events
    event SetIntentXWhitelistChecker( address whitelistChecker);

    /// @notice Initialize the contract
    /// @param _intentxWhitelistChecker address of the new IntentX whitelist checker
    function initialize(
        address _intentxWhitelistChecker
    ) public initializer {
        __Ownable_init();

        intentxWhitelistChecker = _intentxWhitelistChecker;

        emit SetIntentXWhitelistChecker(_intentxWhitelistChecker);
    }

    /// @notice Set the trusted whitelist checker
    /// @param _intentxWhitelistChecker address of the new IntentX whitelist checker
    function setIntentxWhitelistChecker(address _intentxWhitelistChecker) external onlyOwner() {
        intentxWhitelistChecker = _intentxWhitelistChecker;
        emit SetIntentXWhitelistChecker(_intentxWhitelistChecker);
    }

    /// @notice Fill reward for a given day from the token contract
    /// @param _amount amount of reward to fill
    function increaseBridgeBalance(uint256 _amount) external payable {
        require(_amount == msg.value, "Not enough amount sent");

    }

    /*
    function claim(
        address payable _user,
        uint256 _amountMnt,
        uint256 _timestamp,
        bytes memory signature
    ) external {
        require(block.timestamp >= startTimestamp, "NOT STARTED");
        require( msg.sender == _user, "Not your claim");
        require( intentxTrustedAddress != address(0), "IntentX Trusted Address not set yet.");

        if ( _amountMnt > claimed[msg.sender] ) {

            bytes32 messageHash = getMessageHash( _user, _amountMnt, _timestamp);
            bytes32 ethSignedMessageHash = getEthSignedMessageHash(messageHash);

            require( recoverSigner(ethSignedMessageHash, signature) == intentxTrustedAddress, "Signer =! Trusted. ");

            uint256 withdrawableAmount = _amountMnt - claimed[msg.sender];
            claimed[msg.sender] = _amountMnt;

            bool success = _user.send(withdrawableAmount);
            require(success, "MNT Transfer failed");

            emit Claim(msg.sender, block.timestamp , withdrawableAmount);
        }
    }
    */

    function getMessageHash(
        address _user,
        uint _amountIntentX,
        uint _timestamp
    ) public pure returns (bytes32) {
        return keccak256(abi.encodePacked(_user, _amountIntentX, _timestamp));
    }

    function getEthSignedMessageHash(bytes32 _messageHash)
        public
        pure
        returns (bytes32)
    {
        return
            keccak256(
                abi.encodePacked("\x19Ethereum Signed Message:\n32", _messageHash)
            );
    }


    function recoverSigner(bytes32 _ethSignedMessageHash, bytes memory _signature)
        public
        pure
        returns (address)
    {
        (bytes32 r, bytes32 s, uint8 v) = splitSignature(_signature);

        return ecrecover(_ethSignedMessageHash, v, r, s);
    }

    function splitSignature(bytes memory sig)
        public
        pure
        returns (
            bytes32 r,
            bytes32 s,
            uint8 v
        )
    {
        require(sig.length == 65, "invalid signature length");

        assembly {
            r := mload(add(sig, 32))
            s := mload(add(sig, 64))
            v := byte(0, mload(add(sig, 96)))
        }
    }

}

